module.exports = {
  get_blueprint: function(id, err, blueprint){
    Bp.findOne({ id: id }, function(err_, bp) {
      if (err_){
        console.log('Error in Blueprint > get_blueprint: '+err_);
        err(err_);
      }
      if(bp){
        blueprint(bp);
      }
    });
  },
  create_blueprint: function(instance_id, tools, defect_tracker, auth, projects, err, result){
    Bp.create({
      id: instance_id,
      tools: tools,
      defect_tracker: defect_tracker,
      auth: auth,
      projects: projects
    }).done(function(err_, bp) {
      // Error handling
      if (err_) {
        console.log('Error in Blueprint > create_blueprint:'+err_);
        err(err_);
      // The Blueprint was created successfully!
      }else {
        //Blueprint created
        result(bp);
      }
    });
  }
}