'use strict';

var should = require('chai').should();
var maestroApi = require('../index');

describe('#getMetric', function() {
  it('returns a Sensu check output from Maestro Api', function() {
    var node = 'maestro.19m.dev.forj.io';
    var key = 'check-cpu';
    var params = {
        endpoint: 'http://localhost:8087/api/v1.0',
        max_retry: '4',
        timeout: 100
    };
    maestroApi.getMetric(params, node, key, function (err, result){
        should.not.exist(err);
        should.exist(result);
        result.should.not.equal(undefined);
    });
  });
});
