'use strict';

var should = require('chai').should();
var maestro_ldap = require('../maestro-users.js');

describe('#maestrousers', function(){
    it('creates an admin', function(done){
        var user = { full_name: 'kit admin', mail: 'test@gmail.com', password: 'changeme' };
        maestro_ldap.add_admin_user(user, function(err, result){
            should.not.exist(err);
            should.exist(result);
            done();
        });
    });

    it('creates a guest user', function(done){
        var user = { full_name: 'guest user', mail: 'guest-developer@gmail.com', password: 'changeme' };
        maestro_ldap.add_guest_user(user, function(err, result){
            should.not.exist(err);
            should.exist(result);
            done();
        });
    });

    it('retrieves a list of admins', function(done){
        maestro_ldap.get_admins(function(err, users){
            should.not.exist(err);
            should.exist(users);
            done();
        });
    });

    it('retrieves a list of guests', function(done){
        maestro_ldap.get_guest(function(err, users){
            should.not.exist(err);
            should.exist(users);
            done();
        });
    });

    it('moves from guest to developers', function(done){
        var to = 'developers';
        var from = 'guest';
        var mail = 'guest-developer@gmail.com';

        maestro_ldap.change_group(mail, to, from, function(err, result){
            should.not.exist(err);
            should.exist(result);
            done();
        });
    });

    it('retrieves a list of developers', function(done){
        maestro_ldap.get_developers(function(err, users){
            should.not.exist(err);
            should.exist(users);
            done();
        });
    });


});
