"use strict";
/**
 * Backup helper
 */

var maestro_exec = require('maestro-exec/maestro-exec');
var configJson = require('config-json-helper');

module.exports = {
  /*
  callback(error):
    On success: error will be null
    On error: error will be not null
  */
  getProjectsList: function(callback) {

    var gerrit_ip = configJson.getIpBytool('gerrit');
    var cmd = '/usr/bin/ssh -oStrictHostKeyChecking=no -i /opt/config/cacerts/ssh_keys/forjio -p 29418 forjio@'+configJson.getIpBytool('gerrit')+' gerrit ls-projects';

    if (gerrit_ip != "undefined"){
      maestro_exec.execCmd(cmd, function (error, stdout, stderr) {
        if (error){
          console.error('Error running: ' + cmd);
          console.error(stderr);
          callback(null, null, null);
        }else{
          callback(null, stdout.split("\n"), null);
        }
    });
    }else{
       console.error('Config.json doesnt have a Gerrit section');
       callback(null, null, null);
    }
  },

  getGerritIp: function(callback) {
    return(configJson.getIpBytool('gerrit'));
  }
};